import React from 'react';
import ChatBox from './chatBox';
import AddMsg from './addMessage';

function App() {
  const [chatData,setChatData]=React.useState(['fcgfc','gfgfcxgf']);

  const bc= new BroadcastChannel("chatBoox");

  bc.addEventListener("message", e=>{
    setChatData([...chatData,e.data]);
    console.log("msg from broad- "+e);
  })

  const addChat=(msg)=>{
    setChatData([...chatData,msg]);
    bc.postMessage(msg);
    console.log("msg- "+msg);
  };

  return (
    <div>
      <ChatBox chatData={chatData}/>
      <AddMsg chatData={chatData} addChat={addChat}/>
    </div>
  );
}

export default App;
