import logo from './logo.svg';
import './App.css';
import ChatBox from './chatBox';
import AddMsg from './addMessage';

function App() {
  const [chatData,setChatData]=React.useState([]);

  const addChat=(msg)=>{
    setChatData(msg);
  }

  return (
    <div>
      <ChatBox chatData={chatData}/>
      <AddMsg chatData={chatData} addChat={addChat}/>
    </div>
  );
}

export default App;
