import React from "react";

export default function AddMsg({addChat}){
    const [msg,setmsg] = React.useState("");
    const onChange=(event)=>{
        setmsg(event.target.value);
    }
    const onClicked=()=>{
        if(msg===''){
            return
        }
        addChat(msg)
        setmsg("");
    }

    document.getElementById("msgTextArea").addEventListener("keyup",(event)=>{
        event.preventDefault();
        if(event.keyCode === 13){
            document.getElementById("submitButton").click();
        }
    })

    return(
        <div>
            <form>
                <input id="msgTextArea" type='text' onChange={onChange} value={msg}></input>
                <input id="submitButton" type='button' onClick={()=>onClicked()}></input>
            </form>
        </div>
    )
}
