import React from "react";
import {mainDiv} from "./addMessageStyle";
export default function AddMsg({addChat}){
    const [msg,setmsg] = React.useState("");

    const onChange=(event)=>{
        setmsg(event.target.value);
    }
    const onClicked=()=>{
        if(msg===''){
            return
        }
        addChat(msg)
        setmsg("");
    }

    const textAreaElement=document.getElementById("msgTextArea")
    if(null!=textAreaElement){
    textAreaElement.addEventListener("keyup",(event)=>{
        event.preventDefault();
        if(event.key === 13){
            document.getElementById("submitButton").click();
        }
    })
    }
    return(
        <div style={mainDiv()}>
            <form>
                <input id="msgTextArea" type='text' onChange={onChange} value={msg}></input>
                <button id="submitButton" onClick={()=>onClicked()} className="btn">send</button>
            </form>
        </div>
    )
}
